import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AuthContext from "../context/AuthContext";
import "./Welcome.css"; // Custom CSS for the welcome page

const Welcome = () => {
  const { user } = useContext(AuthContext);
  const username = user ? user.email.split('@')[0] : null;

  return (
    <div className="welcome-wrapper">
      {/* Left-side image (hidden on mobile) */}
      <div className="image-section">
        <img src="your-large-image.jpg" alt="Inspiration for Creativity" />
      </div>

      {/* Right-side content */}
      <div className="content-section">
        {user && <h2 className="greeting">Hey, {username}!</h2>}
        <h1 className="headline">Welcome to Techspace</h1>
        <p className="intro-text">
        Step into a hub of innovation, tech insights, and open discussions. Whether you're here to share your expertise or explore fresh perspectives, our community celebrates technology in all its formsâ€”past, present, and future. Join the conversation and let your voice be heard!
        </p>

        {/* If not logged in, encourage users to join */}
        {!user && (
          <Link to="/login">
            <button className="start-button">Join the Vibe</button>
          </Link>
        )}

        {/* Optionally display a special message for logged-in users */}
        {user && (
          <div className="user-content">
            <p>Welcome back, {username}! Your next great post awaits.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Welcome;
