import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AuthContext from "../context/AuthContext";
import "../styles/Welcome.css";

const Welcome = () => {
  const { user } = useContext(AuthContext);
  const username = user ? user.email.split("@")[0] : null;

  return (
      <div className="welcome-wrapper">
        {/* Left-side image */}
        <div className="image-section">
          <img src="blogpic.jpg" alt="People discussing" />
        </div>

        {/* Right-side content */}
        <div className="content-section">

          <h1 className="headline">Tech Vibes</h1>
          <p className="intro-text">
            Step into a hub of innovation, tech insights, and open discussions.
            Whether you're here to share your expertise or explore fresh perspectives,
            our community celebrates technology in all its formsâ€”past, present, and future.
            Join the conversation and let your voice be heard.
          </p>
          <p className="intro-text">
            Here, every question ignites discussion and every idea has the potential to drive breakthrough innovations.
            Whether you're troubleshooting a complex challenge, brainstorming the next big idea, or simply sharing your journey,
            you'll find support and inspiration at every turn!
          </p>

          {/* If not logged in, encourage users to join */}
          {!user && (
            <Link to="/login">
              <button className="start-button">Join the Vibe</button>
            </Link>
          )}

          {/* Special message for logged-in users */}
          {user && (
            <div className="user-content">
              <p>Welcome back, {username}! Your next great post awaits.</p>
            </div>
          )}
        </div>
      <footer>
        <p style={{ marginBottom: "-10px", fontSize: "1.1rem", textAlign: "center" }}>
          <strong>Developed by:</strong> Dr. Kato Samuel Namuene
        </p>
        <p style={{ fontSize: "1.1rem", textAlign: "center" }}>
          <strong>Email:</strong> kato.namuene@ubuea.cm
        </p>
      </footer>
      </div>

  );
};

export default Welcome;
