import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Ensure the post and its ID are available before fetching comments
    if (post && post.id) {
      const fetchComments = async () => {
        try {
          // Fetch comments specifically related to this post
          const response = await api.get(`/comments?postId=${post.id}`);
          setComments(response.data); // Store the comments for the post
        } catch (error) {
          console.error(`Error fetching comments for post ${post.id}:`, error);
        }
      };

      fetchComments();
    }
  }, [post]); // Re-fetch comments if the post changes

  const handleNewComment = (newComment) => {
    // Add the new comment to the post's comment list without refreshing the page
    if (newComment.postId === post.id) {
      setComments((prevComments) => [...prevComments, newComment]);
    }
  };

  return (
    <div style={{ marginBottom: '2rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      {/* Only render the comments section if the post has comments */}
      {comments.length > 0 && (
        <div>
          <h4>Comments:</h4>
          {comments.map((comment) => (
            <div key={comment.id} style={{ marginBottom: '0.5rem' }}>
              <strong>{comment.username}:</strong>
              <p>{comment.content}</p>
            </div>
          ))}
        </div>
      )}

      {/* Always show the comment form below each post */}
      <CommentForm postId={post.id} onCommentSubmitted={handleNewComment} />
    </div>
  );
};

export default PostItem;
