// src/components/PostItem.js
import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  // If post is undefined or missing an id, log an error and render nothing.
  if (!post || !post.id) {
    console.error("PostItem received an invalid post:", post);
    return null;
  }

  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchComments = async () => {
      try {
        console.log(`Fetching comments for post ID: ${post.id}`);
        // Adjust the URL as needed. This version uses query parameters:
        const response = await api.get(`/comments?postId=${post.id}`);
        console.log('Comments response:', response.data);
        setComments(response.data);
      } catch (error) {
        console.error(`Error fetching comments for post ${post.id}:`, error);
      }
    };

    fetchComments();
  }, [post.id]);

  // Callback to add a new comment to state immediately after posting.
  const handleNewComment = (newComment) => {
    setComments((prevComments) => [...prevComments, newComment]);
  };

  return (
    <div style={{ marginBottom: '2rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      <h4>Comments:</h4>
      {comments.length === 0 ? (
        <p>No comments yet.</p>
      ) : (
        comments.map((comment) => (
          <div key={comment.id} style={{ marginBottom: '0.5rem' }}>
            <strong>{comment.username}:</strong>
            <p>{comment.content}</p>
          </div>
        ))
      )}

      <CommentForm postId={post.id} onCommentSubmitted={handleNewComment} />
    </div>
  );
};

export default PostItem;
