import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [showCommentForm, setShowCommentForm] = useState(false);

  useEffect(() => {
    if (!post || !post.id) return;

    const fetchComments = async () => {
      try {
        // Fetch all comments and filter them for this post
        const response = await api.get('/comments');
        const postComments = response.data.filter(
          (comment) => comment.postId === post.id
        );
        setComments(postComments);
      } catch (error) {
        console.error(`Error fetching comments for post ${post.id}:`, error);
      }
    };

    fetchComments();
  }, [post, post.id]);

  // When a new comment is submitted, add it to the list and close the form.
  const handleNewComment = (newComment) => {
    if (newComment.postId === post.id) {
      setComments((prevComments) => [...prevComments, newComment]);
      setShowCommentForm(false);
    }
  };

  // Toggle the visibility of the comment form.
  const toggleCommentForm = () => {
    setShowCommentForm((prevState) => !prevState);
  };

  // Cancel callback to hide the comment form.
  const handleCancelComment = () => {
    setShowCommentForm(false);
  };

  return (
    <div
      style={{
        marginBottom: '2rem',
        borderBottom: '1px solid #ccc',
        paddingBottom: '1rem'
      }}
    >
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      <h4>Comments:</h4>
      {comments.length === 0 ? (
        <p>No comments yet.</p>
      ) : (
        comments.map((comment) => (
          <div key={comment.id} style={{ marginBottom: '0.5rem' }}>
            <strong>{comment.username}:</strong>
            <p>{comment.content}</p>
          </div>
        ))
      )}

      {/* Link to toggle the comment form */}
      <div style={{ marginTop: '1rem' }}>
        <a
          href="#!"
          onClick={toggleCommentForm}
          style={{ cursor: 'pointer', color: '#3498db' }}
        >
          {showCommentForm ? 'Hide Comment Form' : 'Comment'}
        </a>
      </div>

      {/* Conditionally render the comment form */}
      {showCommentForm && (
        <CommentForm
          postId={post.id}
          onCommentSubmitted={handleNewComment}
          onCancel={handleCancelComment}
        />
      )}
    </div>
  );
};

export default PostItem;
