import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom"; // Import useNavigate here
import AuthContext from "../context/AuthContext";
import "../public/login.css"; // Import the login.css stylesheet

const Login = () => {
  const { login } = useContext(AuthContext);
  const navigate = useNavigate(); // Initialize navigate hook in Login component
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Perform login
      await login({ email, password });

      // After successful login, navigate to the PostList page
      navigate("/posts");  // Redirect to /posts after login
    } catch (error) {
      console.error("Login failed:", error);
      alert("Login failed: " + error.message);
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">Login</button>
      </form>
      <div className="footer-links">
        <a href="/forgot-password">Forgot Password?</a>
      </div>
    </div>
  );
};

export default Login;
