import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:3000';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  withCredentials: true, // Allow cookies/auth headers
});

export default api;
// In the above snippet, we create an Axios instance with the base URL set to the API server's URL. We also set the Content-Type header to application/json and enable credentials (cookies and auth headers) with withCredentials: true.
