// src/components/PostItem.js
import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);

  // Fetch comments for the given post
  const fetchComments = async () => {
    try {
      // Adjust the URL if your backend route is different.
      const response = await api.get(`/posts/${post.id}/comments`);
      setComments(response.data);
    } catch (error) {
      console.error(`Error fetching comments for post ${post.id}:`, error);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [post.id]);

  // Handler for when a new comment is submitted.
  const handleNewComment = (newComment) => {
    setComments((prevComments) => [...prevComments, newComment]);
  };

  return (
    <div style={{ marginBottom: '2rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      <h4>Comments:</h4>
      {comments.length === 0 ? (
        <p>No comments yet.</p>
      ) : (
        comments.map((comment) => (
          <div key={comment.id} style={{ marginBottom: '0.5rem' }}>
            <strong>{comment.username}:</strong>
            <p>{comment.content}</p>
          </div>
        ))
      )}

      {/* Pass the handleNewComment callback to update the comment list immediately */}
      <CommentForm postId={post.id} onCommentSubmitted={handleNewComment} />
    </div>
  );
};

export default PostItem;
