import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [showCommentForm, setShowCommentForm] = useState(false);
  const [showComments, setShowComments] = useState(false);

  const user = JSON.parse(localStorage.getItem('user'));

  useEffect(() => {
    if (!post?.id) return; // Simplified null check

    const fetchComments = async () => {
      try {
        const response = await api.get('/comments');
        const postComments = response.data.filter(
          (comment) => comment.postId === post.id
        );
        setComments(postComments);
      } catch (error) {
        console.error(`Error fetching comments for post ${post.id}:`, error);
      }
    };

    fetchComments();
  }, [post.id]); // Only use post.id


  const handleNewComment = (newComment) => {
    if (newComment.postId === post.id) {
      setComments((prevComments) => [...prevComments, newComment]);
      setShowCommentForm(false);
    }
  };

  const toggleCommentForm = () => {
    setShowCommentForm((prevState) => !prevState);
  };

  const toggleCommentsVisibility = () => {
    setShowComments((prevState) => !prevState);
  };

  const handleCancelComment = () => {
    setShowCommentForm(false);
  };

  return (
    <div style={{ marginBottom: '2rem', borderBottom: '1px solid #ccc', paddingBottom: '1rem' }}>
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      <h4>
        Comments ({comments.length})
        {comments.length > 0 && (
          <a href="#!" onClick={toggleCommentsVisibility} style={{ cursor: 'pointer', color: '#3498db', marginLeft: '10px', fontWeight: 'bold' }}>
            {showComments ? 'Hide' : 'Show'}
          </a>
        )}
      </h4>

      {showComments &&
        (comments.length === 0 ? (
          <p>No comments yet.</p>
        ) : (
          comments.map((comment) => (
            <div key={comment.id} style={{ marginBottom: '0.5rem' }}>
              <strong>{comment.username}:</strong>
              <p>{comment.content}</p>
            </div>
          ))
        ))}

      <div style={{ marginTop: '1rem' }}>
        {user ? (
          <a href="#!" onClick={toggleCommentForm} style={{ cursor: 'pointer', color: '#3498db' }}>
            {showCommentForm ? 'Hide Comment Form' : 'Comment'}
          </a>
        ) : (
          <p><a href="/login">Log in</a> to comment.</p>
        )}
      </div>

      {showCommentForm && user && (
        <CommentForm postId={post.id} onCommentSubmitted={handleNewComment} onCancel={handleCancelComment} />
      )}
    </div>
  );
};

export default PostItem;
