import React, { useContext } from "react";
import { Link } from "react-router-dom";
import AuthContext from "../context/AuthContext"; // Import the AuthContext

const Welcome = () => {
  const { user } = useContext(AuthContext); // Get the user from the context

  // Extract the part before "@" from the user's email if logged in
  const username = user ? user.email.split('@')[0] : null;

  return (
    <div className="welcome-container">
      {/* Display Hi <username> only if the user is logged in */}
      {user && <h2>Hi, {username}!</h2>}

      <h2>Welcome to the Commenting App</h2>
      <p>
        This is a simple blog-style commenting application where you can post,
        view, and discuss topics. Join the discussion today!
      </p>

      {/* Conditionally render the "Start Commenting" button only if the user is not logged in */}
      {!user && (
        <Link to="/login">
          <button className="start-button">Start Commenting</button>
        </Link>
      )}

      {/* Optionally display a message or any other content for logged-in users */}
      {user && (
        <div>
          <p>Welcome back, {username}!</p>
          {/* You can add more logged-in user specific content here */}
        </div>
      )}
    </div>
  );
};

export default Welcome;
