import React, { useEffect, useState } from 'react';
import api from '../api';
import CommentForm from './CommentForm';

const PostItem = ({ post }) => {
  const [comments, setComments] = useState([]);
  const [showCommentForm, setShowCommentForm] = useState(false);

  useEffect(() => {
    if (!post || !post.id) return;

    const fetchComments = async () => {
      try {
        const response = await api.get(`/comments?postId=${post.id}`);
        setComments(response.data);
      } catch (error) {
        console.error(`Error fetching comments for post ${post.id}:`, error);
      }
    };

    fetchComments();
  }, [post]);

  // When a new comment is submitted, add it to the list and hide the form.
  const handleNewComment = (newComment) => {
    setComments((prevComments) => [...prevComments, newComment]);
    setShowCommentForm(false);
  };

  // Toggle the visibility of the comment form.
  const toggleCommentForm = () => {
    setShowCommentForm((prev) => !prev);
  };

  return (
    <div className="post-item">
      <h3>{post.title}</h3>
      <p>{post.content}</p>

      <h4>Comments:</h4>
      {comments.length === 0 ? (
        <p>No comments yet.</p>
      ) : (
        comments.map((comment) => (
          <div key={comment.id} className="comment">
            <strong>{comment.username}:</strong>
            <p>{comment.content}</p>
          </div>
        ))
      )}

      {/* Toggle between the "Comment" link and the CommentForm */}
      {showCommentForm ? (
        <CommentForm postId={post.id} onCommentSubmitted={handleNewComment} />
      ) : (
        <a
          href="#"
          className="toggle-comment-link"
          onClick={(e) => {
            e.preventDefault();
            toggleCommentForm();
          }}
        >
          Comment
        </a>
      )}
    </div>
  );
};

export default PostItem;
