import React, { useEffect, useState } from 'react';
import api from '../api';
import PostItem from './PostItem';

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const token = localStorage.getItem('token'); // Adjust based on your auth system
        const response = await api.get('/posts', {
          headers: {
            Authorization: `Bearer ${token}`, // Include token in request
          },
        });

        console.log('Fetched Posts:', response.data);
        setPosts(response.data);
      } catch (error) {
        console.error('Failed to fetch posts:', error);
        if (error.response) {
          setError(`Server Error: ${error.response.status} - ${error.response.data?.message || 'Unauthorized'}`);
        } else if (error.request) {
          setError('Failed to fetch posts. No response from server.');
        } else {
          setError('An unknown error occurred.');
        }
      }
    };

    fetchPosts();
  }, []);  

  return (
    <div>
    <h2>Blog Posts</h2>
    {error && <p style={{ color: 'red' }}>{error}</p>}
    {posts.length === 0 ? (
      <p>No posts found.</p> // Adjusted message
    ) : (
      posts.map((post) => {
        console.log('Displaying Post:', post); // Debugging log
        return <PostItem key={post.id} post={post} />;
      })
    )}
  </div>
  );
};

export default PostList;
