// AuthContext.js (example snippet)
import React, { createContext, useState } from 'react';
import api from '../api';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const login = async ({ email, password }) => {
    const response = await api.post('/login', { email, password });
    const token = response.data.token;
    // Save token in localStorage for api.js interceptor to pick it up
    localStorage.setItem('token', token);
    // Optionally decode token or use response data to set the user
    const loggedInUser = { email }; // Replace with actual user details from response if available
    setUser(loggedInUser);
    localStorage.setItem('user', JSON.stringify(loggedInUser));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
