import React, { useContext } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import { AuthProvider } from "./context/AuthContext";
import AuthContext from "./context/AuthContext";
import Welcome from "./pages/Welcome"; // Import Welcome Page
import PostList from "./components/PostList";
import Login from "./pages/Login";
import Register from "./pages/Register";
import "./App.css";

const Navbar = () => {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate(); // Get navigate for redirection

  const handleLogout = (e) => {
    e.preventDefault(); // Prevent default navigation
    logout();
    navigate("/"); // Redirect to Welcome page after logout
  };

  return (
    <nav>
      <Link to="/">Home</Link> | 
      {user ? (
        <>
          <Link to="/posts">Posts</Link> | 
          <Link to="/" onClick={handleLogout} className="navbar-link logout-button">
            Logout
          </Link>
        </>
      ) : (
        <>
          <Link to="/login">Login</Link> | 
          <Link to="/register">Register</Link>
        </>
      )}
    </nav>
  );
};

function App() {
  const { user } = useContext(AuthContext); // Get user status

  return (
    <AuthProvider>
      <Router>
        <div className="container">
          <header className="my-4 text-center">
            <h1>Technology Blog</h1>
            <Navbar />
          </header>

          <Routes>
            <Route path="/" element={user ? <PostList /> : <Welcome />} /> 
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/posts" element={<PostList />} /> 
          </Routes>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
